// src/components/OrderHistory.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OrderHistory.css'; // Make sure to create this CSS file for styling

const API_BASE_URL = 'http://localhost:8090/api/orders';

const OrderHistory = ({ userId }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/user/${userId}`);
        setOrders(response.data);
      } catch (error) {
        setError('Error fetching order history');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, [userId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="order-history">
      <h2>Order History</h2>
      {orders.length === 0 ? (
        <p>No previous orders found.</p>
      ) : (
        <ul>
          {orders.map(order => (
            <li key={order.id} className="order-item">
              <h3>Order ID: {order.id}</h3>
              <p>Date: {new Date(order.date).toLocaleDateString()}</p>
              <p>Total: â‚¹{order.total.toFixed(2)}</p>
              <p>Status: {order.status}</p>
              <button onClick={() => handleViewOrderDetails(order.id)}>View Details</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const handleViewOrderDetails = (orderId) => {
  // Navigate to a detailed view or show a modal with order details
  alert(`Viewing details for order ID: ${orderId}`);
};

export default OrderHistory;
